import React,{useState} from 'react';
import {useNavigate} from 'react-router-dom';
import axios from 'axios';

function Add(){

    const navigate=useNavigate();
    const [newUser,setNewUser]=useState({
        username:'',
        email:'',
        dob:'',
    })

    const [setErrors,setErros]=useState({
        username:'',
        email:'',
        dob:'',
    })

    
    const handleChange= (e) => {
        const{name,value}=e.target;
        setNewUser((prev) =>({
            ...prev,
            [name]:value
        }));

        setErrors((prev) => ({
            ...prev,[name]:''
        }))
    };
    const handleSubmit =  async(e) =>{
        e.preventDefault();

        if(newUser.username.trim() === '')
        {
            alert('Username is required');
            return;
        }    
        else if(newUser.username.trim().length < 3)
        {
            alert('Username must be atleast 3 characters');
            return;
        }  
        
        if(newUser.email.trim() === '')
        {
            alert('Email is required');
            return;
        }    
        



        await axios.post('http://localhost:5000/api/user',newUser);

        alert('User added Successfully');

        setNewUser({username:'',email:'',dob:''});
        navigate('/');

    }
        
    return(
        <div>
            <center> <h1> Add User </h1> </center>

            <form onSubmit={handleSubmit}>
                Username : &nbsp; &nbsp; &nbsp; 
                <input
                name="username"
                placeholder="Enter Username"
                value={newUser.username}
                onChange={handleChange}
                required
                />
                <br/> <br/>

                Email : &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  &nbsp; &nbsp;
                <input
                name="email"
                placeholder="Enter Email"
                value={newUser.email}
                onChange={(e) => setNewUser({...newUser,email:e.target.value})}
                required
                />
                <br/> <br/>

                DOB &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;
                <input
                type="date"
                name="dob"
                placeholder="Enter DOB"
                value={newUser.dob}
                onChange={(e) => setNewUser({...newUser,dob:e.target.value})}
                required
                />
                <br/> <br/>

                <button type="submit"> Add</button>

                <button type="button" onClick={(e) => setNewUser({username:'',email:'',dob:''})}> Reset</button>



            </form>

        </div>

    );
}

export default Add;
